CREATE VIEW "public"."v_payees" AS (select "payees"."id", COALESCE("accounts"."name", "payees"."name") as "name", "payees"."transfer_acct", "payees"."favorite", "payees"."learn_categories", "payees"."tombstone" from "payees" left join "accounts" on ("payees"."transfer_acct" = "accounts"."id" and "accounts"."tombstone" IS FALSE) where ("payees"."transfer_acct" is null or "accounts"."id" is not null));